import { Component } from '@angular/core';
import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';


@Component({
  selector: 'app-restaurantlogin',
  templateUrl: './userlogin.component.html',
  styleUrls: ['./userlogin.component.css']
})
export class UserloginComponent {

  email = '';
  password = '';

  constructor(private http: HttpClient, private router: Router,     private authService: AuthService ) {}

  login(): void {
    if ( !this.email || !this.password ) {
      alert('Please provide all the required details.');
      return; 
    }

    const loginData = { 
      email: this.email, 
      password: this.password 
    };
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    this.http.post('https://8080-cdcccaeacaaacfcdbccbacbfccbbebfcae.project.examly.io/login', loginData, { headers, observe: 'response' }).subscribe(
      (response) => {
        if (response.status === 200) {
          // Login successful, redirect to the Home component
          this.authService.setAuthenticated(true, 'user'); // Set the user type as 'admin'

          this.router.navigate(['/home']);
        } 
        },
      (error: HttpErrorResponse) => {
        if (error.status === 401) {
          // Show an alert for invalid credentials
          alert('Invalid credentials. Please try again.');
        } else {
          console.log(error);
        }
      }
    );
  }
}

<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.2/css/all.min.css"/>
<div class="body">
<div class="container">
<div class="wrapper">
  <div class="title"><span>User Login</span></div>

  <form (ngSubmit)="login()">

    <div class="form-floating mb-3">
      <input type="email" class="form-control" id="floatingInput" name="email" placeholder="name@example.com" [(ngModel)]="email">
      <label for="floatingInput"> <i class="fas fa-user"></i>  Email address</label>
    </div>

    <div class="form-floating mb-3">
      <input type="password" class="form-control" id="floatingInput" name="email" placeholder="name@example.com" [(ngModel)]="password">
      <label for="floatingInput"> <i class="fas fa-lock"></i> Password</label>
    </div>

    <div>
      <input type="submit" id="login" value="Login">
    </div>
    <div class="signup-link">Not a member? <a [routerLink]="['/usersignup']">Signup now</a></div>
  </form>
</div>
</div>
</div>
