import { Component } from '@angular/core';
import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-usersignup',
  templateUrl: './usersignup.component.html',
  styleUrls: ['./usersignup.component.css']
})
export class UsersignupComponent {
  name: string = '';
  email: string = '';
  password: string = '';
  city: string = '';
  street: string = '';
  state: string = '';
  zipcode: string = '';

  constructor(private http: HttpClient, private router: Router) {}

  register(): void {
    if (!this.name || !this.email || !this.password || !this.street || !this.city || !this.state || !this.zipcode) {
      alert('Please provide all the required details.');
      return;
    }

    const addressData = {
      street: this.street,
      city: this.city,
      state: this.state,
      zipCode: this.zipcode
    };

    const customerData = {
      name: this.name,
      email: this.email,
      password: this.password,
      addressId: addressData
    };

    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    this.http.post('https://8080-cdcccaeacaaacfcdbccbacbfccbbebfcae.project.examly.io/customer', customerData, { headers, responseType: 'text' }).subscribe(
      (response: any) => {
        console.log(response);
        if (response.includes('created') || response.includes('Customer created')) {
          alert('Registration successful!');
          this.router.navigate(['/userlogin']);
        } else if (response.includes('already registered')) {
          alert('Email is already registered. Please use a different email.');
        } else {
          alert('Registration failed. Please try again later.');
        }
      },
      (error: HttpErrorResponse) => {
        console.log(error);
      }
    );
  }
}
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.2/css/all.min.css"/>
<div class="body">
  <div class="container">  
    <div class="title">User Registration</div>
    <div class="content">

      <form (submit)="register()" #signupForm="ngForm">      
        <div class="user-details">

          <div class="form-group">
            <label for="name">Name</label>
            <input type="text" id="name" name="name" class="form-control" [(ngModel)]="name" placeholder="Enter your name">
          </div>

          <div class="form-group">
            <label for="email">Email</label>
            <input type="email" id="email" name="email" class="form-control"  [(ngModel)]="email" placeholder="Enter your email">
          </div>

          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" name="password" class="form-control" [(ngModel)]="password" placeholder="Enter your password">
          </div>

          <div class="form-group">
            <label for="street">Street</label>
            <input type="text" id="street" name="street" class="form-control" [(ngModel)]="street" placeholder="Enter your street">
          </div>

          <div class="form-group">
            <label for="city">City</label>
            <input type="text" id="city" name="city" class="form-control" [(ngModel)]="city" placeholder="Enter your city">
          </div>

          <div class="form-group">
            <label for="state">State</label>
            <input type="text" id="state" name="state" class="form-control" [(ngModel)]="state" placeholder="Enter your state">
          </div>

          <div class="form-group">
            <label for="zipcode">Zipcode</label>
            <input type="text" id="zipcode" name="zipcode" class="form-control" [(ngModel)]="zipcode" placeholder="Enter your zipcode">
          </div>

        </div> <!--user details ending-->

        <div>
          <input type="submit" value="Sign up">
        </div>
        <div class="signup-link">Already a member? <a [routerLink]="[('/userlogin')]">Login here</a></div>
        <br>
      </form>
    </div> <!--content ending-->
  </div> <!--container ending-->
</div>
