import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Order {
  id: number;
  customerId: number;
  menuItemId: number;
  totalCost: number;
  deliveryAddress: string;
  deliveryTime: string;
  restaurantId: number;
  status: string;
}

@Component({
  selector: 'app-ordertracking',
  templateUrl: './ordertracking.component.html',
  styleUrls: ['./ordertracking.component.css']
})
export class OrdertrackingComponent implements OnInit {
  order: Order | undefined;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.fetchOrderDetails();
  }

  fetchOrderDetails() {
    const orderId = 1; 
    this.http.get<Order>(`https://8080-aaabcaeedefcbacfcdbceaeaadbdbabf.project.examly.io/GET_Order`)
      .subscribe(
        (response) => {
          this.order = response;
        },
        (error) => {
          console.error('Failed to fetch order details:', error);
        }
      );
  }
}
<body>
    <div class="after-form-spacing"> </div>
    <div class="container">

        <div *ngIf="order" class="card">

          <div class="card-body">
            <h2 class="text-center header">Order Status</h2>
    <p><strong>Order ID:</strong> {{ order.id }}</p>
    <p><strong>Customer ID:</strong> {{ order.customerId }}</p>
    <p><strong>Menu Item ID:</strong> {{ order.menuItemId }}</p>
    <p><strong>Total Cost:</strong> {{ order.totalCost }}</p>
    <p><strong>Delivery Address:</strong> {{ order.deliveryAddress }}</p>
    <p><strong>Delivery Time:</strong> {{ order.deliveryTime }}</p>
    <p><strong>Restaurant ID:</strong> {{ order.restaurantId }}</p>
    <p><strong>Status:</strong> {{ order.status }}</p>
    <a href="https://www.google.com/maps/@16.3050267,80.4215138,15z" class="btn btn-info track-order-button">Track my order</a>
  </div>
  </div>
  <div *ngIf="!order" class="text-center mt-3">
    <p>Loading order details...</p>
  </div>
</div>
</body>
