import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-contactus',
  templateUrl: './contactus.component.html',
  styleUrls: ['./contactus.component.css']
})
export class ContactusComponent {
  constructor(private http: HttpClient) {}

  submitForm(event: Event) {
    event.preventDefault(); // Prevent default form submission behavior

    const nameInput = <HTMLInputElement>document.getElementById('name');
    const emailInput = <HTMLInputElement>document.getElementById('email');
    const messageInput = <HTMLInputElement>document.getElementById('message');

    const formData = {
      name: nameInput.value,
      email: emailInput.value,
      message: messageInput.value
    };

    // Check if any field is empty
    if (!formData.name || !formData.email || !formData.message) {
      alert('Please provide all the required details.');
      return; // Stop the form submission
    }

    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      })
    };

    this.http.post('https://8080-cdcccaeacaaacfcdbccbacbfccbbebfcae.project.examly.io/contactus', formData, httpOptions)
      .subscribe(
        () => {
          // Handle success response
          console.log('Form submitted successfully.');
          alert('Form submitted successfully.');
        },
        (error) => {
          // Handle error response
          console.error('Error occurred while submitting the form:', error);
        }
      );
  }
  }
<div class="contact-us">
  <div class="contact-form">
    <form (submit)="submitForm($event)">
      <h2 class="text-center ">Contact Us</h2>
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" id="name" name="name" class="form-control" placeholder="Enter your name" required>
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input type="email" id="email" name="email" class="form-control" placeholder="Enter your email" required>
      </div>

      <div class="form-group">
        <label for="message">Message</label>
        <textarea id="message" name="message" placeholder="Type your complaint here..." class="form-control" rows="5" required></textarea>
      </div>

      <button type="submit">Submit form</button>
    </form>
  </div>
</div>
